package hello;

public class GreaterNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=51, b=7, c=8;
		if(a>b && a>c) {
			System.out.println("A is greatest");
		}
		else if(b>a && b>c) {
			System.out.println("B is greatest");
		}
		else {
			System.out.println("C is greatest");
		}
	}

}
