package hello;
public class CelsiusintoFahrenheit {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double c = 13;
		double f = 32+((9*c)/5);
		System.out.println("Fahrenhit is " + f);
		c = (5*(f-32))/9;
		System.out.println("Celsius is "+ c);
		int a=2;
		float b=4.0f;
		int  m =(int) Math.pow(a, b);
		System.out.println(m);
		

	}

}
